import $ from "../jquery-global.min.js";
export default function( options )
{
let settings = $.extend( {
'selectorForClose': '.or-view'
}, options);
let button = this;
$( settings.selectorForClose ).click( function() {
});
return $(this)
.addClass('button--is-watched')
.click( function() {
$(this).toggleClass('button--is-active');
} ) ;
};